mapper.select:
SELECT top 1 id, token, refresh_token, token_type, expired,revoked,user_id
FROM tokens

    AND refresh_token = #{refresh_token} 
    AND token = #{token} 
    AND user_id = #{user_id} 
    AND expired = #{expired} 
    AND revoked = #{revoked}

mapper.update:
update tokens
revoked = #{revoked}, expired = #{expired},
where id = #{id}
